import "../App.css";
import { useHistory } from "react-router-dom";
import AppContext from "../context/AppContext";
import { useContext, useState } from "react";
import firebase from "../lib/firebase";

function Profile() {
  const appContext = useContext(AppContext);
  const history = useHistory();

  const [loginUserEmail, setLoginUserEmail] = useState();
  const [loginUserDisplayName, setLoginUserDisplayName] = useState();
  const [loginUserId, setLoginUserId] = useState();
  const [loginUserPhotoURL, setLoginUserPhotoURL] = useState();

  firebase.auth().onAuthStateChanged((user) => {
    if (user) {
      // User is signed in, see docs for a list of available properties
      // https://firebase.google.com/docs/reference/js/firebase.User
      console.log(user.email);
      setLoginUserEmail(user.email);
      // ...
    } else {
      // User is signed out
      // ...
      history.push("/login");
    }

    if (user !== null) {
      user.providerData.forEach((profile) => {
        console.log("Sign-in provider: " + profile.providerId);
        console.log("  Provider-specific UID: " + profile.uid);
        console.log("  Name: " + profile.displayName);
        console.log("  Email: " + profile.email);
        console.log("  Photo URL: " + profile.photoURL);
        setLoginUserDisplayName(profile.displayName);
        appContext.setUser(profile.displayName);
      });
    }
  });

  const handleChangeUser = (e) => {
    appContext.setUserInput(e.target.value);
    appContext.setUserInputFilled(true);
  };

  const handleUserSubmit = () => {
    //appContext.setUser(appContext.userInput);

    const user = firebase.auth().currentUser;

    console.log("user")

    // Create a Storage Ref w/ username
    let storageRef = firebase
      .storage()
      .ref(user + "/profilePicture/" + file.name);

    // Upload file
    let task = storageRef.put(file);

    user
      .updateProfile({
        displayName: appContext.userInput,
        photoURL: "https://example.com/jane-q-user/profile.jpg",
      })
      .then(() => {
        // Update successful
        // ...
        console.log("Update successful");
      })
      .catch((error) => {
        // An error occurred
        // ...
      });

    appContext.setUserInputFilled(false);

    history.push("/");
  };

  return (
    <div className="profile-wrapper">
      <div className="profile-left"></div>
      <div className="profile-container">
        <div className="profile-title">Profile</div>
        <div className="profile-subtitle">User Name</div>
        {!loginUserDisplayName && (
          <div>
            <input
              className="profile-input"
              type="text"
              onChange={handleChangeUser}
              placeholder="Create a username"
            />
          </div>
        )}
        {loginUserDisplayName && (
          <div>
            <input
              className="profile-input"
              type="text"
              onChange={handleChangeUser}
              placeholder={loginUserDisplayName}
            />
          </div>
        )}
        <div className="profile-subtitle">Email</div>
        <div>
          <input className="profile-input" type="text" value={loginUserEmail} />
        </div>
        <div className="profile-subtitle">Upload Profile's picture</div>
        <div>
          <input type="file" id="myFile" name="filename" />
        </div>

        <div className="profile-button-cotainer">
          <button
            disabled={!appContext.userInputFilled}
            className={`submit-button-${!appContext.userInputFilled}`}
            onClick={handleUserSubmit}
          >
            Save
          </button>
        </div>
      </div>
      <div className="profile-right"></div>
    </div>
  );
}

export default Profile;
