import React, { useContext, useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import List from "./components/List";
import Form from "./components/Form";
import "./App.css";
import AppContext from "./context/AppContext";
import firebase from "./lib/firebase";
import cryptoRandomString from 'crypto-random-string';



function Home() {
  const appContext = useContext(AppContext);
  let location = useLocation();
  const [displayName, setDisplayName] = useState();


  const userLogin = firebase.auth().currentUser;

  if (userLogin !== null) {
    // The userLogin object has basic properties such as display name, email, etc.
    setDisplayName(userLogin.displayName);
    const email = userLogin.email;
    const photoURL = userLogin.photoURL;
    const emailVerified = userLogin.emailVerified;

    // The user's ID, unique to the Firebase project. Do NOT use
    // this value to authenticate with your backend server, if
    // you have one. Use User.getToken() instead.
    const uid = userLogin.uid;

  }

  useEffect(() => {
    if (userLogin !== null){
    console.log(userLogin.displayName, userLogin.email );
    }
  }, [])


  useEffect(() => {
    // After user logs in, get user object from successful login
    if (!appContext.user) {
      // do some user stuff
      //console.log(appContext.user);
      //console.log(location);
      const pagePathname = location.pathname;
      if (!pagePathname.includes("signup")) {
        appContext.setRedirect("/login");
      }
    }
    //console.log(appContext.redirect);
  }, [appContext.user, location]);

  useEffect(() => {
    if (appContext.carsLeft < 0) {
      appContext.setIsFormComplete(false);
      appContext.setAlert(true);
    } else if (appContext.formData && appContext.formData["content"]) {
      appContext.setIsFormComplete(true);
      appContext.setAlert(false);
    } else {
      appContext.setIsFormComplete(false);
      appContext.setAlert(false);
    }
  });

  useEffect(() => {
    const newFormData = {
      content: appContext.noteInput,
      // tweetId: cryptoRandomString({length: 10}),
      userName: displayName,
    };

    appContext.setFormData(newFormData);
    // eslint-disable-next-line
  }, [appContext.noteInput]);

  return (
    <div className="App">
      <Form />
      <List />
    </div>
  );
}

export default Home;
