import { useContext, useEffect, useState, useRef, useCallback } from "react";
import "../App.css";
import axios from "axios";
import AppContext from "../context/AppContext";
import firebase from "../lib/firebase";

function List() {
  const appContext = useContext(AppContext);
  const [tweetArrayDB, setTweetArrayDB] = useState([]);

  const observer = useRef();
  const lastTweetRef = useCallback()

  useEffect(() => {
    // axios
    //   .get(
    //     "https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet"
    //   )
    //   .then((res) => {
    //     appContext.setTweetsDB(res.data.tweets);
    //   });

    const dbRef = firebase.database().ref();
    dbRef
      .child("tweets")
      .get()
      .then((snapshot) => {
        if (snapshot.exists()) {
          //console.log(snapshot.val());
          //console.log(Object.values(snapshot.val()));

          setTweetArrayDB(Object.values(snapshot.val()));
        } else {
          console.log("No data available");
        }
      })
      .catch((error) => {
        console.error(error);
      });
  });

  // useEffect(() => {
  //   console.log(tweetArrayDB);
  // }, [])

  // setTweetArrayDB((prevState) => {
  //   return [Object.values(value), ...prevState];
  // });

  let orderedList = tweetArrayDB.sort(function (a, b) {
    return new Date(b.date) - new Date(a.date);
  });

  var perChunk = 10; // items per chunk

  var result = orderedList.reduce((resultArray, item, index) => {
    const chunkIndex = Math.floor(index / perChunk);

    if (!resultArray[chunkIndex]) {
      resultArray[chunkIndex] = []; // start a new chunk
    }

    resultArray[chunkIndex].push(item);

    return resultArray;
  }, []);

  //console.log(result);

  return (
    <div className="list">
      {/* List */}

      {result.map((e) => {

        e.map((tweet) => {
          console.log(e);
          //console.log(tweet);

          return (
            <div ref={lastTweetRef} className="list_item" key={tweet.id}>
              <div className="list-item-data">
                <div>{tweet.userName}</div>
                {<div>{tweet.date}</div>}
              </div>
              {<div className="list-item-tweet">{tweet.content}</div>}
            </div>
          );

        })





      })}
    </div>
  );
}

export default List;
