import { useContext, useEffect } from "react";
import "../App.css";
import axios from "axios";
import AppContext from "../context/AppContext";

function List() {
  const appContext = useContext(AppContext);

  useEffect(() => {
    axios
      .get(
        "https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet"
      )
      .then((res) => {
        appContext.setTweetsDB(res.data.tweets);
      });
  });

  let orderedList = appContext.tweetsDB.sort(function (a, b) {
    return new Date(b.createdDate) - new Date(b.createdDate);
  });


  return (
    <div className="list">
      {/* List */}
      {appContext.tweetsDB.reverse().map((e) => {
        return (
          <div className="list_item" key={e.id}>
            <div className="list-item-data">
              <div>{e.userName}</div>
              {<div>{e.date}</div>}
            </div>
            {<div className="list-item-tweet">{e.content}</div>}
          </div>
        );
      })}
    </div>
  );
}

export default List;
