    j,
    temporary,
    chunk = 10;
  for (i = 0, j = orderedList.length; i < j; i += chunk) {
    temporary = orderedList.slice(i, i + chunk);
    // do whatever
    console
  }

  return (
    <div className="list">
      {/* List */}

      {orderedList.map((e) => {
        return (
          <div className="list_item" key={e.id}>
            <div className="list-item-data">
              <div>{e.userName}</div>
              {<div>{e.date}</div>}
            </div>
            {<div className="list-item-tweet">{e.content}</div>}
          </div>
        );
      })}
    </div>
  );
}

export default List;
