import React, { useContext, useEffect } from "react";
import { useLocation } from "react-router-dom";
import List from "./components/List";
import Form from "./components/Form";
import "./App.css";
import AppContext from "./context/AppContext";
import firebase from "./lib/firebase";
import { useHistory } from "react-router-dom";
import InfiniteScroll from "react-infinite-scroll-component";

function Home() {
  const appContext = useContext(AppContext);
  const history = useHistory();
  let location = useLocation();

  const userLogin = firebase.auth().currentUser;

  if (userLogin !== null) {
    // The userLogin object has basic properties such as display name, email, etc.
    const displayName = userLogin.displayName;
    const email = userLogin.email;
    const photoURL = userLogin.photoURL;
    const emailVerified = userLogin.emailVerified;

    // The user's ID, unique to the Firebase project. Do NOT use
    // this value to authenticate with your backend server, if
    // you have one. Use User.getToken() instead.
    const uid = userLogin.uid;
  } else {
    // User is signed out
    // ...
    history.push("/login");
  }

  // useEffect(() => {
  //   if (userLogin !== null){
  //   console.log(userLogin.displayName, userLogin.email );
  //   }
  // }, [])

  useEffect(() => {
    // After user logs in, get user object from successful login
    if (!appContext.user) {
      // do some user stuff
      //console.log(appContext.user);
      //console.log(location);
      const pagePathname = location.pathname;
      if (!pagePathname.includes("signup")) {
        appContext.setRedirect("/login");
      }
    }
    //console.log(appContext.redirect);
  }, [appContext.user, location]);

  useEffect(() => {
    if (appContext.carsLeft < 0) {
      appContext.setIsFormComplete(false);
      appContext.setAlert(true);
