import { useContext, useEffect, useState } from "react";
import "../App.css";
import axios from "axios";
import AppContext from "../context/AppContext";
import firebase from "../lib/firebase";

function List() {
  const appContext = useContext(AppContext);
  const [tweetArrayDB, setTweetArrayDB] = useState();

  useEffect(() => {
    // axios
    //   .get(
    //     "https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet"
    //   )
    //   .then((res) => {
    //     appContext.setTweetsDB(res.data.tweets);
    //   });

    const dbRef = firebase.database().ref();
    dbRef
      .child("users")
      .get()
      .then((snapshot) => {
        if (snapshot.exists()) {
          console.log(snapshot.val());
          setTweetArrayDB(snapshot.val());
        } else {
          console.log("No data available");
        }
      })
      .catch((error) => {
        console.error(error);
      });
  });

  useEffect(() => {
    tweetArrayDB.get()
    console.log(tweetArrayDB);
  }, [tweetArrayDB])

  let orderedList = appContext.tweetsDB.sort(function (a, b) {
    return new Date(b.createdDate) - new Date(a.createdDate);
  });

  return (
    <div className="list">
      {/* List */}
      {orderedList.map((e) => {
        return (
          <div className="list_item" key={e.id}>
            <div className="list-item-data">
              <div>{e.userName}</div>
              {<div>{e.date}</div>}
            </div>
            {<div className="list-item-tweet">{e.content}</div>}
          </div>
        );
      })}
    </div>
  );
}

export default List;
