import { useContext, useEffect, useState } from "react";
import "../App.css";
import axios from "axios";
import AppContext from "../context/AppContext";
import firebase from "../lib/firebase";
import InfiniteScroll from "react-infinite-scroll-component";


function List() {
  const appContext = useContext(AppContext);
  const [tweetArrayDB, setTweetArrayDB] = useState([]);

  useEffect(() => {
    // axios
    //   .get(
    //     "https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet"
    //   )
    //   .then((res) => {
    //     appContext.setTweetsDB(res.data.tweets);
    //   });

    const dbRef = firebase.database().ref();
    dbRef
      .child("tweets")
      .get()
      .then((snapshot) => {
        if (snapshot.exists()) {
          //console.log(snapshot.val());
          //console.log(Object.values(snapshot.val()));

          setTweetArrayDB(Object.values(snapshot.val()));
        } else {
          console.log("No data available");
        }
      })
      .catch((error) => {
        console.error(error);
      });
  });

  // useEffect(() => {
  //   console.log(tweetArrayDB);
  // }, [])

  // setTweetArrayDB((prevState) => {
  //   return [Object.values(value), ...prevState];
  // });

  let orderedList = tweetArrayDB.sort(function (a, b) {
    return new Date(b.date) - new Date(a.date);
  });

  return (
    <InfiniteScroll
        dataLength={orderedList.length} //This is important field to render the next data
        next={this.fetchData}
        hasMore={true}
        loader={<h4>Loading...</h4>}
        endMessage={
          <p style={{ textAlign: "center" }}>
            <b>Yay! You have seen it all</b>
          </p>
        }
      >
    <div className="list">
      {/* List */}
      {orderedList.map((e) => {
        return (
          <div className="list_item" key={e.id}>
            <div className="list-item-data">
              <div>{e.userName}</div>
              {<div>{e.date}</div>}
            </div>
            {<div className="list-item-tweet">{e.content}</div>}
          </div>
        );
      })}
    </div>
    </InfiniteScroll>
  );
}

export default List;
