import "../App.css";
import { Link, useHistory } from "react-router-dom";
import AppContext from "../context/AppContext";
import React, { useContext, useState } from "react";
import firebase from "../lib/firebase";

function Navbar() {
  const appContext = useContext(AppContext);
  const history = useHistory();
  const [logged, setLogged] = useState(false);

  const userLogin = firebase.auth().currentUser;

      if (userLogin !== null) {
        // The userLogin object has basic properties such as display name, email, etc.
        const displayName = userLogin.displayName;
        const email = userLogin.email;
        const photoURL = userLogin.photoURL;
        const emailVerified = userLogin.emailVerified;

        // The user's ID, unique to the Firebase project. Do NOT use
        // this value to authenticate with your backend server, if
        // you have one. Use User.getToken() instead.
        const uid = userLogin.uid;

        setLogged(true);

        history.push("/profile");
      } else {
        // User is signed out
        // ...
        history.push("/login");
      }

  const handleLogout = () => {
    firebase
      .auth()
      .signOut()
      .then(() => {
        // Sign-out successful.
        console.log("Sign-out successful");
        appContext.setUser("");
        history.push("/login");
      })
      .catch((error) => {
        // An error happened.
      });
  };

  return (
    <div className="navbar-wrapper">
      <div className="navbar">
        <div className="navbar-left">
          {appContext.user && (
            <div>
              <Link className="navbar-option" to="/">
                Home
              </Link>
            </div>
          )}
          {logged && (
            <div>
              <Link className="navbar-option" to="/profile">
                Profile
              </Link>
            </div>
          )}
        </div>
        <div className="navbar-right">
          <div>
            {!logged && (
              <Link className="navbar-option" to="/login">
                Log in
              </Link>
            )}
            {logged && (
              <div className="navbar-option" onClick={handleLogout}>
                Log out
              </div>
            )}
          </div>
          <div>
            {!logged && (
              <Link className="navbar-option" to="/signup">
                Sign up
              </Link>
            )}
          </div>
        </div>
      </div>
    </div>
  );
  //   }
}

export default Navbar;
