import { useContext } from "react";
import "../App.css";
import { formatISO } from "date-fns";
import AppContext from "../context/AppContext";
import firebase from "../lib/firebase";



function Form() {
  const appContext = useContext(AppContext);

  const userLogin = firebase.auth().currentUser;

  if (userLogin !== null) {
    // The userLogin object has basic properties such as display name, email, etc.
    const displayName = userLogin.displayName;
    const email = userLogin.email;
    const photoURL = userLogin.photoURL;
    const emailVerified = userLogin.emailVerified;

    // The user's ID, unique to the Firebase project. Do NOT use
    // this value to authenticate with your backend server, if
    // you have one. Use User.getToken() instead.
    const uid = userLogin.uid;

  }

  const handleChangeNote = (e) => {
    const charCount = e.target.value.length;
    let charLeft = 140 - charCount;
    appContext.setCarsLeft(charLeft);
    appContext.setNoteInput(e.target.value);
  };

  // useEffect(() => {
  //   console.log(appContext.carsLeft);
  // }, [appContext.carsLeft]);

  const handleSubmit = () => {
    appContext.setIsFormComplete(false);
    appContext.setLoadingActive(true);
    setTimeout(() => {
      if (appContext.carsLeft > 0) {
        const finalData = appContext.formData;

        finalData["date"] = formatISO(new Date(), "dd-MM-yyyy HH:mm:ss");

        appContext.setNotesArray((prevState) => {
          return [finalData, ...prevState];
        });

        appContext.setNoteInput("");
        appContext.setFormData({});

        //console.log(finalData);

        const userLogin = firebase.auth().currentUser;
        if (userLogin !== null) {
          // The userLogin object has basic properties such as display name, email, etc.
          const displayName = userLogin.displayName;
          const email = userLogin.email;
          const photoURL = userLogin.photoURL;
          const emailVerified = userLogin.emailVerified;

          // The user's ID, unique to the Firebase project. Do NOT use
          // this value to authenticate with your backend server, if
          // you have one. Use User.getToken() instead.
          const uid = userLogin.uid;



          firebase
          .database()
          .ref("tweets/" + userLogin.uid +"_"+ new Date())
          .set(finalData);
        }

        // axios.post('https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet', finalData)
        // .then(res => {
        //   console.log(res);
        // })
        // .catch(err => {
        //   console.error(err);
        // })

        // localStorage.setItem(finalData["id"], JSON.stringify(finalData));
        appContext.setLoadingActive(false);
      }
    }, 500);
  };

  return (
    <div className="form-wrapper">
      <textarea
        className="tweet-form"
        onChange={handleChangeNote}
        type="text"
        placeholder="What you have in mind..."
        value={appContext.noteInput}
      />
      <div className="tweet-bottom">
        <div>
          <div className={`tweet-error-${appContext.alert}`}>
            The tweet can't contain more then 140 chars.
          </div>
        </div>
        <div> </div>
        <div className="tweet-button">
          <img
            className={`loading-${appContext.loadingActive}`}
            src="/loadingLogo.svg"
            alt="loading"
          ></img>
          <button
            disabled={!appContext.isFormComplete}
            className={`submit-button-${!appContext.isFormComplete} tweet`}
            onClick={handleSubmit}
          >
            Tweet
          </button>
        </div>
      </div>
    </div>
  );
}

export default Form;
